import './App.css';
import 'semantic-ui-css/semantic.min.css'
import React, { useState } from 'react'
import { Dropdown, Input, Grid, Button, Form, Select, Container, Segment, Divider} from 'semantic-ui-react'
import { getOptions, getMetrics, getMeasures, getTimeOptions, getOperatorOptions } from './Utility';
import 'react-datez/dist/css/react-datez.css';
import { ReactDatez } from 'react-datez'


function App() {
  const [startDate, setStartDate] = useState("")
  const [endDate, setEndDate] = useState("")
  const [restaurantIds, setRestaurantIds] = useState([]);
  const [fromHour, setFromHour] = useState(6);
  const [toHour, setToHour] = useState(29);
  const [criteria, setCriteria] = useState([{ metricCode: "", compareType : "", value: 0, operatorType: "" }])
  const [myJson, setMyJson] = useState([])

let handleChange = (index, data) => {
    let newCriteria = [...criteria];
    if (data.name === "value") {
        if (data.value === '') {
            newCriteria[index][data.name] = '';
        }
        else {
            newCriteria[index][data.name] = parseInt(data.value);
        }
    }
    else {
        newCriteria[index][data.name] = data.value;
    }
    setCriteria(newCriteria);
    fixDate()
}

let addCriteria = () => {
    setCriteria([...criteria, { metricCode: "", compareType : "", value: 0, operatorType: "" }])
}

let removeCriteria = (i) => {
    let newCriteria = [...criteria];
    newCriteria.splice(i, 1);
    setCriteria(newCriteria)
}

let onSubmit = () => {
    userPush()
}

let fixDate = () => {
        var temp = startDate
        var tempr = endDate
    if (temp.length > 19) {
        for (var i=0;i<6;i++) {
            temp = temp.slice(0, -1)
            tempr = tempr.slice(0, -1)
        }
        console.log(temp)
        setStartDate(temp)
        setEndDate(tempr)
    }
}

let createData = () => {
    const initialFormData = {
        restaurantIds: restaurantIds,
        fromDate: startDate,
        toDate: endDate,
        fromHour: fromHour,
        toHour: toHour,
        metricCriteria: criteria
    };
    const myJsons = JSON.stringify(initialFormData)
    return myJsons
}

let timeFix = (dat) => {
    let x = dat.substring(11,16)
    return x
}

const userPush = async () => {
    let xxx = createData()
    const response = await fetch('https://customsearchqueryapi.azurewebsites.net/Search/Query', {
      method: 'POST',
      body: xxx, // string or object
      headers: {
        'Content-Type': 'application/json'
      }
    });
    let temp = await response.json() //extract JSON from the http response
    let tempr = []
    for (var i=0;i<temp.length;i++) {
        tempr.push(temp[i])
    }
    setMyJson(tempr)
  }

  return (
    <div className="App">
            <Grid>
                <Grid.Row>
                    <Container>
                        <Segment className="Segment">
                            <Grid centered>
                                <Grid.Row columns="1">
                                    <Grid.Column textAlign="center">
                                        <h3>Custom Search Query Tool</h3>
                                    </Grid.Column>
                                </Grid.Row>
                                <Grid.Row columns="1">
                                    <Grid.Column>
                                        <Form onSubmit={() => onSubmit()} >
                                            <Form.Field>
                                                <label style={{fontWeight: "bold"}}>Restaurant Id</label>
                                                <Dropdown
                                                    options={getOptions}
                                                    placeholder={"Select Restaurant Id"}
                                                    multiple
                                                    selection
                                                    onChange={(event, data) => setRestaurantIds(data.value)}
                                                    value={restaurantIds}
                                                />
                                            </Form.Field>
                                            <Form.Group>
                                                <Form.Field
                                                    control={Select}
                                                    label={"Transaction Time Start"}
                                                    options={getTimeOptions}
                                                    value={fromHour}
                                                    onChange={(event, data) => setFromHour(data.value)}
                                                />
                                                <Form.Field
                                                    control={Select}
                                                    label={"Transaction Time End"}
                                                    options={getTimeOptions}
                                                    value={toHour}
                                                    onChange={(event, data) => setToHour(data.value)}
                                                />
                                                <Form.Field>
                                                <label htmlFor="exampleDate2">Start Date</label>
                                                <ReactDatez
                                                    name="dateInput"
                                                    startDate="01/01/2000"
                                                    endDate= {new Date()}
                                                    handleChange={value => {
                                                    setStartDate(value);
                                                    }}
                                                    value={startDate}
                                                    allowPast= { true }
                                                    dateFormat= "MM/DD/YYYY"
                                                />
                                                </Form.Field>
                                                <Form.Field>
                                                <label htmlFor="exampleDate2">End Date</label>
                                                <ReactDatez
                                                    name="dateInput"
                                                    startDate="01/01/2000"
                                                    endDate= {new Date()}
                                                    handleChange={value => {
                                                    setEndDate(value);
                                                    }}
                                                    value={endDate}
                                                    allowPast= { true }
                                                    dateFormat= "MM/DD/YYYY"
                                                />
                                                </Form.Field>
                                            </Form.Group>
                                            {criteria.map((crit, index) => (
                                                    <Form.Group>
                                                        <Form.Field
                                                            name="metricCode"
                                                            control={Select}
                                                            options={getMetrics}
                                                            label={"Metric to Measure by"}
                                                            placeholder='Choose Metric'
                                                            value={crit.metricCode}
                                                            onChange={(event, data) => handleChange(index, data)}
                                                        />
                                                        <Form.Field
                                                            name="compareType"
                                                            control={Select}
                                                            label={"Measure"}
                                                            options={getMeasures}
                                                            placeholder='Measure'
                                                            onChange={(event, data) => handleChange(index, data)}
                                                            value={crit.compareType}
                                                        />
                                                        <Form.Field
                                                            name="value"
                                                            control={Input}
                                                            label={"Value"}
                                                            placeholder='Value'
                                                            onChange={(event, data) => handleChange(index, data)}
                                                            value={crit.value}
                                                        />
                                                        <Form.Field
                                                            name="operatorType"
                                                            control={Select}
                                                            label={"Operator"}
                                                            options={getOperatorOptions}
                                                            placeholder='Operator Options'
                                                            onChange={(event, data) => handleChange(index, data)}
                                                            value={crit.operatorType}
                                                        />
                                                        <Form.Field>
                                                            {
                                                                index ?
                                                                <Button onClick={() => removeCriteria(index)}  color="red">Remove</Button>
                                                                : null
                                                            }
                                                        </Form.Field>
                                                    </Form.Group>
                                                ))}
                                            <Form.Group>
                                                <Form.Field>
                                                    <Button className='ui button' onClick={() => addCriteria()} color="violet">Add Criteria</Button>
                                                </Form.Field>
                                            </Form.Group>
                                            <Form.Field>
                                                <Button color="olive" type="submit">
                                                    Submit
                                                </Button>
                                            </Form.Field>
                                        </Form>
                                    </Grid.Column>
                                </Grid.Row>
                            </Grid>
                        </Segment>
                    </Container>
                </Grid.Row>
                <Divider hidden></Divider>
                <Grid.Row>
                    <Container>
                        <Segment>
                            <h3>Results</h3>
                            <table class="ui celled table">
                                <thead>
                                    <tr>
                                    <th>Order Number</th>
                                    <th>Resturant ID</th>
                                    <th>Date</th>
                                    <th>Net Amount</th>
                                    <th>Total Amount</th>
                                    <th>Items Sold</th>
                                    <th>Beverage Quantity</th>
                                    <th>Discount Amount</th>
                                    <th>Discount Ratio</th>
                                    <th>Deleted Items</th>
                                    <th>Order Time</th>
                                    <th>Refund Amount</th>
                                </tr></thead>
                                    {myJson.map((criti, index) => (
                                        <tbody>
                                            <tr>
                                            <td data-label="Order Number">{myJson[index].orderNumber}</td>
                                            <td data-label="Resturant ID">{myJson[index].restaurantId}</td>
                                            <td data-label="Date">{myJson[index].busDt}</td>
                                            <td data-label="Net Amount">{"$" + myJson[index].netAmount.toFixed(2)}</td>
                                            <td data-label="Total Amount">{"$" + myJson[index].totalAmount.toFixed(2)}</td>
                                            <td data-label="Items Sold">{myJson[index].itemSoldQty}</td>
                                            <td data-label="Beverage Quantity">{myJson[index].beverageQty}</td>
                                            <td data-label="Discount Amount">{"$" + myJson[index].discountAmount.toFixed(2)}</td>
                                            <td data-label="Discount Ratio">{(myJson[index].discountRatio * 100).toFixed(2) + "%"}</td>
                                            <td data-label="Deleted Items">{myJson[index].itemDeletedAmount}</td>
                                            <td data-label="order Time">{timeFix(myJson[index].orderTime)}</td>
                                            <td data-label="Refund Amount">{"$" + myJson[index].refundAmount.toFixed(2)}</td>
                                            </tr>
                                        </tbody>
                                    ))}
                                </table>
                        </Segment>
                    </Container>
                </Grid.Row>
            </Grid>
        </div>
  );
}

export default App;
