import './App.css';
import 'semantic-ui-css/semantic.min.css'
import React, { useState, useEffect } from 'react'
import { Dropdown, Input, Grid, Button, Form, Select, Container, Segment, Divider } from 'semantic-ui-react'
import { getOptions, getMetrics, getMeasures, getTimeOptions, getOperatorOptions } from './Utility';
import 'react-datez/dist/css/react-datez.css';
import { ReactDatez } from 'react-datez'


function App() {
  const [startDate, setStartDate] = useState("")
  const [endDate, setEndDate] = useState("")
  const [restaurantIds, setRestaurantIds] = useState([]);
  const [fromHour, setFromHour] = useState(6);
  const [toHour, setToHour] = useState(29);
  const [criteria, setCriteria] = useState([{ metricCode: "", compareType : "", value: 0, operatorType: "" }])

let handleChange = (index, data) => {
    let newCriteria = [...criteria];
    newCriteria[index][data.target.name] = data.target.value;
    setCriteria(newCriteria);
    console.log(index)
    console.log(data.target.name)
}

let addCriteria = () => {
    setCriteria([...criteria, { metricCode: "", compareType : "", value: 0, operatorType: "" }])
}

let removeCriteria = (i) => {
    let newCriteria = [...criteria];
    newCriteria.splice(i, 1);
    setCriteria(newCriteria)
}

let onSubmit = () => {
    userPush()
}

let createData = () => {
    const initialFormData = {
        restaurantIds: restaurantIds,
        fromDate: startDate,
        toDate: endDate,
        fromHour: fromHour,
        toHour: toHour,
        metricCriteria: criteria
    };
    const myJson = JSON.stringify(initialFormData)
    return myJson
}
const userPush = async () => {
    const response = await fetch('https://customsearchqueryapi.azurewebsites.net/Search/Query', {
      method: 'POST',
      body: createData(), // string or object
      headers: {
        'Content-Type': 'application/json'
      }
    });
    const myJson = await response.json(); //extract JSON from the http response
    console.log(createData())
  }

const userPull = async () => {
    const response = await fetch('https://customsearchqueryapi.azurewebsites.net/SEARCH/MetricDefinitions');
    const myJson = await response.json(); //extract JSON from the http response
  }

  useEffect(() => {
      userPull()
    console.log("Work");
}, []);

  return (
    <div className="App">
            <Grid>
                <Grid.Row>
                    <Container>
                        <Segment className="Segment">
                            <Grid centered>
                                <Grid.Row columns="1">
                                    <Grid.Column textAlign="center">
                                        <h3>Custom Search Query Tool</h3>
                                    </Grid.Column>
                                </Grid.Row>
                                <Grid.Row columns="1">
                                    <Grid.Column>
                                        <Form onSubmit={() => onSubmit()} >
                                            <Form.Field>
                                                <label style={{fontWeight: "bold"}}>Restaurant Id</label>
                                                <Dropdown
                                                    options={getOptions}
                                                    placeholder={"Select Restaurant Id"}
                                                    multiple
                                                    selection
                                                    onChange={(event, data) => setRestaurantIds(data.value)}
                                                    value={restaurantIds}
                                                />
                                            </Form.Field>
                                            <Form.Group>
                                                <Form.Field
                                                    control={Select}
                                                    label={"Transaction Time Start"}
                                                    options={getTimeOptions}
                                                    value={fromHour}
                                                    onChange={(event, data) => setFromHour(data.value)}
                                                />
                                                <Form.Field
                                                    control={Select}
                                                    label={"Transaction Time End"}
                                                    options={getTimeOptions}
                                                    value={toHour}
                                                    onChange={(event, data) => setToHour(data.value)}
                                                />
                                                <Form.Field>
                                                <label htmlFor="exampleDate2">Start Date</label>
                                                <ReactDatez
                                                    name="dateInput"
                                                    startDate="01/01/2000"
                                                    endDate= {new Date()}
                                                    handleChange={value => {
                                                    setStartDate(value);
                                                    }}
                                                    value={startDate}
                                                    allowPast= { true }
                                                    dateFormat= "MM/DD/YYYY"
                                                />
                                                </Form.Field>
                                                <Form.Field>
                                                <label htmlFor="exampleDate2">End Date</label>
                                                <ReactDatez
                                                    name="dateInput"
                                                    startDate="01/01/2000"
                                                    endDate= {new Date()}
                                                    handleChange={value => {
                                                    setEndDate(value);
                                                    }}
                                                    value={endDate}
                                                    allowPast= { true }
                                                    dateFormat= "MM/DD/YYYY"
                                                />
                                                </Form.Field>
                                            </Form.Group>
                                            {criteria.map((crit, index) => (
                                                    <Form.Group>
                                                        <Form.Field
                                                            name='metricCode'
                                                            control={Select}
                                                            options={getMetrics}
                                                            label={"Metric to Measure by"}
                                                            placeholder='Choose Metric'
                                                            onChange={data => handleChange(index, data)}
                                                            value={crit.metricCode}
                                                        />
                                                        <Form.Field
                                                            control={Select}
                                                            label={"Measure"}
                                                            options={getMeasures}
                                                            placeholder='Measure'
                                                            onChange={data => handleChange(index, data)}
                                                            value={crit.compareType}
                                                        />
                                                        <Form.Field
                                                            control={Input}
                                                            label={"Value"}
                                                            placeholder='Value'
                                                            onChange={data => handleChange(index, data)}
                                                            value={crit.value}
                                                        />
                                                        <Form.Field
                                                            control={Select}
                                                            label={"Operator"}
                                                            options={getOperatorOptions}
                                                            placeholder='Operator Options'
                                                            onChange={data => handleChange(index, data)}
                                                            value={crit.operatorType}
                                                        />
                                                        <Form.Field>
                                                            {
                                                                index ?
                                                                <Button onClick={() => removeCriteria(index)}  color="red">Remove</Button>
                                                                : null
                                                            }
                                                        </Form.Field>
                                                    </Form.Group>
                                                ))}
                                            <Form.Group>
                                                <Form.Field>
                                                    <Button className='ui button' onClick={() => addCriteria()} color="violet">Add Criteria</Button>
                                                </Form.Field>
                                            </Form.Group>
                                            <Form.Field>
                                                <Button color="olive" type="submit" onClick={() => addCriteria()}>
                                                    Submit
                                                </Button>
                                            </Form.Field>
                                        </Form>
                                    </Grid.Column>
                                </Grid.Row>
                            </Grid>
                        </Segment>
                    </Container>
                </Grid.Row>
                <Divider hidden></Divider>
                <Grid.Row>
                    <Container>
                        <Segment>
                            <h3>Results</h3>
                        </Segment>
                    </Container>
                </Grid.Row>
            </Grid>
        </div>
  );
}

export default App;
